import subprocess
import time
import threading


def f_288(file_list):
    """
    Run files from list of files as subprocesses at the same time.
    
    Parameters:
    - file_list (list of str): List of files name to run.

    Returns:
    list: The exit codes of the subprocesses.

    Requirements:
    - subprocess
    - time
    - threading

    Example:
    >>> f_288(["f_288_data/file1.bat", "f_288_data/file2.bat"])
    [0, 0]
    """
    exit_codes = []
    threads = []

    def run_file(file):
        process = subprocess.Popen(file, stdout=subprocess.PIPE)
        exit_codes.append(process.wait())

    for file in file_list:
        thread = threading.Thread(target=run_file, args=(file,))
        thread.start()
        threads.append(thread)

    for thread in threads:
        thread.join()

    return exit_codes


