import os
import os.path
import csv
import collections


# Constants
FILE_NAME = 'file_sizes.csv'

def f_262(my_path):
    """
    Create a report on the file size in a directory and write it to a CSV file.

    Parameters:
    my_path (str): The directory path.

    Returns:
    str: The path of the CSV file.

    Requirements:
    - os
    - os.path
    - csv
    - collections

    Example:
    >>> f_262('/usr/my_directory')
    """

    # Initialize a dictionary to store file sizes
    file_sizes = collections.defaultdict(int)

    # Iterate through all files in the directory and its subdirectories
    for root, dirs, files in os.walk(my_path):
        for file in files:
            file_path = os.path.join(root, file)
            file_size = os.path.getsize(file_path)
            file_sizes[file_path] = file_size

    # Write the file sizes to a CSV file
    with open(FILE_NAME, 'w', newline='') as csv_file:
        writer = csv.writer(csv_file)
        writer.writerow(['File Path', 'File Size'])
        for file_path, file_size in file_sizes.items():
            writer.writerow([file_path, file_size])

    return FILE_NAME


