    df_unique['cluster'] = kmeans.labels_

    # Plot the clustered data
    fig, ax = plt.subplots()
    scatter = ax.scatter(df_unique['x'], df_unique['y'], c=df_unique['cluster'])

    return duplicate_counts, df_unique, ax


