    # Set the x-axis label
    ax.set_xlabel('Index')
    
    # Set the y-axis label
    ax.set_ylabel('Value')
    
    # Set the plot title
    ax.set_title('Values from dictionaries')
    
    # Return the Axes object
    return ax


