    axes[0].set_title('Histogram of Scores')
    
    # Plot boxplot of scores by country
    sns.boxplot(x='Country', y='Score', data=unique_df, ax=axes[1])
    axes[1].set_title('Boxplot of Scores by Country')
    
    # Adjust layout
    plt.tight_layout()
    
    return fig


