    # Plot the circle on the polar axes
    ax.plot(theta, np.full_like(theta, radius), linestyle='-', linewidth=2)

    # Set the radial ticks
    ax.set_yticks(np.linspace(0, radius, num=5))

    # Return the modified Axes object
    return ax


