from datetime import datetime, timedelta
import pytz
import numpy as np
import matplotlib.pyplot as plt


def f_383(start_time, end_time):
    """
    Plots the hourly difference between UTC and specified global time zones across a date range.

    This function visualizes the time difference in hours between UTC and predefined time zones for each day
    within the specified date range. Predefined time zones include UTC, America/Los_Angeles, Europe/Paris,
    Asia/Kolkata, and Australia/Sydney. The differences are plotted on a graph, using a distinct color for
    each time zone's time difference curve, selecting from ["b", "g", "r", "c", "m", "y", "k"].

    Parameters:
    - start_time (str): The start date in the format "yyyy-mm-dd".
    - end_time (str): The end date in the format "yyyy-mm-dd".

    Returns:
    - matplotlib.axes.Axes: The Axes object with the plotted time differences in hours between UTC and 
                            other time zones.

    Requirements:
    - datetime.datetime
    - datetime.timedelta
    - pytz
    - numpy
    - matplotlib.pyplot

    Example:
    >>> ax = f_383('2021-01-01', '2021-01-10')
    >>> type(ax)
    <class 'matplotlib.axes._axes.Axes'>
    >>> ax.get_xticklabels()
    [Text(18628.0, 0, '2021-01-01'), Text(18629.0, 0, '2021-01-02'), Text(18630.0, 0, '2021-01-03'), Text(18631.0, 0, '2021-01-04'), Text(18632.0, 0, '2021-01-05'), Text(18633.0, 0, '2021-01-06'), Text(18634.0, 0, '2021-01-07'), Text(18635.0, 0, '2021-01-08'), Text(18636.0, 0, '2021-01-09')]
    """

    # Convert start_time and end_time to datetime objects
    start_date = datetime.strptime(start_time, "%Y-%m-%d")
    end_date = datetime.strptime(end_time, "%Y-%m-%d")

    # Define the time zones to compare
    time_zones = ["UTC", "America/Los_Angeles", "Europe/Paris", "Asia/Kolkata", "Australia/Sydney"]

    # Create a list to store the time differences for each time zone
    time_differences = []

    # Iterate over each time zone
    for time_zone in time_zones:
        # Create a list to store the time differences for the current time zone
        time_differences_for_time_zone = []

        # Iterate over each day in the date range
        current_date = start_date
        while current_date <= end_date:
            # Get the UTC time for the current date
            utc_time = pytz.utc.localize(current_date)

            # Get the time in the current time zone for the current date
            current_time_zone = pytz.timezone(time_zone)
            current_time_zone_time = utc_time.astimezone(current_time_zone)

            # Calculate the time difference in hours between UTC and the current time zone
            time_difference = (current_time_zone_time - utc_time).total_seconds() / 3600

            # Append the time difference to the list for the current time zone
            time_differences_for_time_zone.append(time_difference)

            # Increment the current date by one day
            current_date += timedelta(days=1)

        # Append the list of time differences for the current time zone to the overall list
        time_differences.append(time_differences_for_time_zone)

    # Create a list of colors to use for each time zone's time difference curve
    colors = ["b", "g", "r", "c", "m", "y", "k"]

    # Create a list of dates for the x-axis
    dates = [start_date + timedelta(days=i) for i in range((end_date - start_date).days + 1)]

    # Create a list of x-axis values in matplotlib format
    x_axis_values = [date.toordinal() for date in dates]

    # Create a list of x-axis labels in the format "yyyy-mm-dd"
    x_axis_labels = [date.strftime("%Y-%m-%d") for date in dates]

    # Create a figure and axes
    fig, ax = plt.subplots()

    # Plot the time differences for each time zone
    for i, time_differences_for_time_zone in enumerate(time_differences):
        ax.plot(x_axis_values, time_differences_for_time_zone, color=colors[i], label=time_zones[i])

    # Set the x-axis ticks and labels
    ax.set_xticks(x_axis_values)
    ax.set_xticklabels(x_axis_labels, rotation=45)

    # Set the y-axis label
    ax.set_ylabel("Time Difference (hours)")

    # Set the title
    ax.set_title("Time Difference Between UTC and Other Time Zones")

    # Add a legend
    ax.legend()

    # Return the Axes object
    return ax



