import pandas as pd
import string

def f_777(word):
    """
    Creates a Pandas DataFrame from a single word, where each row contains a letter from the word 
    and its 1-based position in the alphabet.

    Requirements:
    - pandas
    - string
    
    Parameters:
    - word (str): The word to create the DataFrame from. The word should be in lowercase and consist of alphabetic characters only.
    
    Returns:
    - pandas.DataFrame: A DataFrame with two columns: 'Letter' and 'Position', 
      where 'Position' is the letter's position in the English alphabet.
    
    Examples:
    >>> f_777('abc')
      Letter  Position
    0      a         1
    1      b         2
    2      c         3

    >>> f_777('zoo')
      Letter  Position
    0      z        26
    1      o        15
    2      o        15
    
    Raises:
    - ValueError: If the input word is not in lowercase or contains non-alphabetic characters.
    """

    # Check if the word is in lowercase
    if not word.islower():
        raise ValueError("The input word must be in lowercase.")

    # Check if the word contains only alphabetic characters
    if not word.isalpha():
        raise ValueError("The input word must consist of alphabetic characters only.")

    # Create a DataFrame with the letter and its position in the alphabet
    df = pd.DataFrame({'Letter': list(word), 'Position': [string.ascii_lowercase.index(letter) + 1 for letter in word]})

    return df


