from datetime import datetime
import random
import matplotlib.pyplot as plt


def f_390(
    epoch_milliseconds,
    teams=["Team1", "Team2", "Team3", "Team4", "Team5"],
    random_seed=0,
):
    """
    Generate and plot a performance trend for different teams from a given epoch timestamp to the current time.

    The performance data is generated by creating a series of random values for each day from the starting timestamp
    to the present day. Each team's performance is simulated as a random float between 0.1 and 1 for each day.
    The plot shows days since the start date on the x-axis and performance on the y-axis.

    Parameters:
    epoch_milliseconds (int): The epoch milliseconds from where to start the generation. Must not be in the future.
    teams (list of str, optional): Team names. If not provided, defaults to ['Team1', 'Team2', 'Team3', 'Team4', 'Team5'].
    random_seed (int, optional): Seed for random number generation to ensure reproducibility. Defaults to 0.

    Returns:
    dict: A dictionary containing performance data for each team, with days as indices and performance as float values.
    matplotlib.figure.Figure: A figure object showing the performance trend of each team over the days.

    Requirements:
    - datetime.datetime
    - random
    - matplotlib

    Example:
    >>> results, ax = f_390(1236472051807)
    >>> results.keys()
    dict_keys(['Team1', 'Team2', 'Team3', 'Team4', 'Team5'])
    >>> type(ax)
    <class 'matplotlib.figure.Figure'>
    """

    # Check if the epoch_milliseconds is in the future
    if epoch_milliseconds > datetime.now().timestamp() * 1000:
        raise ValueError("The epoch_milliseconds must not be in the future.")
    # Convert epoch_milliseconds to datetime object
    start_date = datetime.fromtimestamp(epoch_milliseconds / 1000)
    # Set the random seed for reproducibility
    random.seed(random_seed)
    # Create a dictionary to store the performance data for each team
    results = {}
    # Iterate over the teams and generate performance data for each team
    for team in teams:
        # Create a list to store the performance data for the current team
        performance = []
        # Iterate over the days from the start date to the current date
        for day in range(int((datetime.now() - start_date).days)):
            # Generate a random performance value for the current day
            performance.append(random.uniform(0.1, 1))
        # Add the performance data for the current team to the results dictionary
        results[team] = performance
    # Create a figure and axes for the plot
    fig, ax = plt.subplots()
    # Plot the performance data for each team
    for team, data in results.items():
        ax.plot(data, label=team)
    # Set the x-axis label and ticks
    ax.set_xlabel("Days since start date")
    ax.set_xticks(range(len(data)))
    # Set the y-axis label
    ax.set_ylabel("Performance")
    # Set the title
    ax.set_title("Performance Trend")
    # Add a legend
    ax.legend()
    # Show the plot
    plt.show()
    # Return the results dictionary and the figure object
    return results, fig



