import re
import pandas as pd


def f_381(df: pd.DataFrame, column_name: str, pattern: str) -> pd.DataFrame:
    """
    Reverse the order of words in a specific column of a pandas DataFrame where the words
    match a user-specified regular expression pattern, using a nested helper function.
    Words are considered to be whitespace-separated strings. This function maintains the
    original order of non-matching words.

    Parameters:
    - df (pd.DataFrame): The pandas DataFrame.
    - column_name (str): The name of the column to be modified.
    - pattern (str), the regular expression pattern to match words against.

    Returns:
    - pd.DataFrame: A new pandas DataFrame with the specified column's words reordered
    if they match the pattern, maintaining the original order of words that do not match,
    and returning a copy of the unaltered DataFrame if the pattern is empty.

    Requirements:
    - pandas
    - re

    Example:
    >>> df = pd.DataFrame({'A': ['apple orange', 'red yellow green'], 'B': [1, 2]})
    >>> pattern = r'\b(?:apple|yellow)\b'
    >>> reversed_df = f_381(df, 'A', pattern)
    >>> reversed_df
                      A  B
    0      apple orange  1
    1  red yellow green  2
    >>> df = pd.DataFrame({'A': ['yellow car red', 'green apple yellow'], 'B': [3, 4]})
    >>> pattern = r'\b(?:car|apple|yellow)\b'
    >>> reversed_df = f_381(df, 'A', pattern)
    >>> reversed_df
                        A  B
    0      yellow car red  3
    1  green apple yellow  4
    """

    def reverse_words(words: list[str], pattern: str) -> list[str]:
        """
        Reverse the order of words in a list that match a user-specified regular expression pattern.
        Words are considered to be whitespace-separated strings.

        Parameters:
        - words (list[str]): The list of words to be reversed.
        - pattern (str), the regular expression pattern to match words against.

        Returns:
        - list[str]: A new list with the words reversed if they match the pattern, maintaining the
        original order of words that do not match.

        Requirements:
        - re

        Example:
        >>> words = ['apple', 'orange', 'yellow', 'green']
        >>> pattern = r'\b(?:apple|yellow)\b'
        >>> reversed_words = reverse_words(words, pattern)
        >>> reversed_words
        ['yellow', 'orange', 'apple', 'green']
        >>> words = ['red', 'yellow', 'green', 'blue']
        >>> pattern = r'\b(?:car|apple|yellow)\b'
        >>> reversed_words = reverse_words(words, pattern)
        >>> reversed_words
        ['red', 'yellow', 'green', 'blue']
        """
        # Split the string into words
        words = words.split()
        # Find the indices of words that match the pattern
        matched_indices = [i for i, word in enumerate(words) if re.search(pattern, word)]
        # Reverse the order of words that match the pattern
        reversed_words = [words[i] for i in matched_indices[::-1]]
        # Insert the reversed words back into the original list
        for i, index in enumerate(matched_indices):
            words.insert(index, reversed_words[i])
            words.pop(index + 1)
        return words

    # Check if the pattern is empty
    if not pattern:
        return df

    # Apply the reverse_words function to the specified column
    df[column_name] = df[column_name].apply(lambda x: " ".join(reverse_words(x, pattern)))
    return df



